import React from 'react';
import CategoryCard, {Category} from "./CategoryCard";


interface CategoryListProps {
    category_list: Category[];
    status?: boolean;
    onSelectCategory?: (category: Category) => void;
}



const CategoryList = ({ category_list, status, onSelectCategory}: CategoryListProps) => {
    return (
        <>
            <div className='flex flex-wrap gap-4'>
                {category_list.map((category: Category) => <CategoryCard name={category.name} active={status}
                                                                         onSelect={()=> onSelectCategory ? onSelectCategory(category) : null } />)}
            </div>
        </>
    )
}

export default CategoryList;
