import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { PhoneVerification } from "../../interface/request/Authorization/PhoneVerification";
import { RegistrationDtoRequest } from "../../interface/request/Authorization/RegistrationDtoRequest";

export default function Verification() {
  const navigate = useNavigate();
  const location = useLocation();
  const [phoneCode, setPhoneCode] = useState("");

  const finalRegistration = async (response: any) => {
    const pathVerification = "/api/v1/otp/verify";

    const verificationRequest: PhoneVerification = {
      phone: signUp.phone,
      code: phoneCode,
      otp_type: 2,
    };
    let token = "";
    await api
      .post(pathVerification, verificationRequest)
      .then((data) => {
        token = data.data.data.token;
        console.log(token);
      })
      .catch(function (error) {
        console.log(error);
        setCodeError(true);
      });

    const path = "/api/v1/register";

    const request: RegistrationDtoRequest = {
      token: token,
      phone: signUp.phone,
      password: signUp.password,
      confirm_password: signUp.confirmPassword,
    };
    await api
      .post(path, request)
      .then((data) => {
        console.log(data);

        if (data.status === 200) {
          navigate("/home");
        }
      })
      .catch(function (error) {
        console.log(error);
        setCodeError(true);
      });
  };

  return (
    <>
      <div className="h-screen  flex items-center justify-center flex-col  bg-gradient-to-r from-pink-100 via-purple-100 to-gray-100 w-full h-screen font-abel">
        <div className="text-center w-1/2  h-2/5">
          <div className="text-5xl mb-4">Verify Your number</div>
          <div className="text-3xl">
            Enter the 4 digit code we sent to your phone.
          </div>
          {/* {codeError && <p style={{ color: "red" }}>Wrong code!</p>} */}
          <div className="w-full h-1/2 flex items-center justify-center ">
            <input
              className="w-2/12 h-3/6 mx-5 text-4xl pl-6 rounded-xl"
              type="text"
              maxLength={4}
              value={phoneCode}
              onChange={(event) => {
                setPhoneCode(event.target.value);
              }}
            />
          </div>

          <div className=" w-4/12 h-12 m-auto mb-4">
            <button
              type="button"
              className=" w-full h-full bg-dark-blue text-center text-white rounded-xl flex justify-center items-center"
              onClick={() => {
                navigate("/home");
              }}
            >
              VERIFY
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
