import React, { useState } from 'react';
import {PlusIcon} from "../../../Icons/Icons";
import {upload} from "@testing-library/user-event/dist/upload";

const EventDetails = () => {
    const [img, setImg] = useState<File []>()

    function handleImg(e: any) {
        const selectedFile = e.target.files?.[0];

        if (selectedFile) {
            setImg(selectedFile);
        }
    }

    return (
        <>
           <div className='mx-auto w-[1000px] font-inter'>
                <div className='w-full h-[40px] text-2xl border-b-[1px] border-b-gray-200'>General Info</div>
                <div className='flex mt-[15px]'>
                    <form className='w-[600px]'>
                        <label className='block mb-1'>Event Name</label>
                        <input className='w-[460px] h-[50px] block outline-none bg-gray-100 rounded-[10px] p-5 mb-[20px]' placeholder='Event name'/>
                        <label className='block mb-1'>Event Description</label>
                        <textarea className='outline-none bg-gray-100 rounded-[10px] w-[460px] p-5' rows={3} placeholder='Event descrption'/>
                    </form>
                    <div
                        className='w-[380px] h-[220px] border-dotted border-[1px] border-black my-auto flex justify-center items-center'>
                        <input
                            type="file"
                            accept="image/*"
                            style={{display: 'none'}}
                            onChange={handleImg}
                            id="uploadInput"
                        />
                        <label htmlFor='uploadInput'><PlusIcon/></label>
                    </div>
                </div>


           </div>
        </>
    )
}

export default EventDetails;
