import React from "react";
import { create } from "zustand";
import { persist } from "zustand/middleware";

interface Token {
  jwt: string;
  setToken: (token: string) => void;
}

export const useTokenStore = create<Token>(
  //persist(
  (set, get) => ({
    jwt: " ",
    setToken: (token: string) => {
      set({ jwt: token });
    },
  })
  // { name: "jwt-storage" }
  //)
);
