import React, {useState} from 'react';

export interface Category {
    id?: number;
    name: string;
    active?: boolean;
    onSelect?: () => void;
    categoryCardClassName?: string;
}

const CategoryCard = ({ name, onSelect, categoryCardClassName }: Category) => {
    const [active, setActive] = useState(false);
    const handleSelect = () => {
        setActive(prev => !prev);
        if (onSelect) {
            console.log(1)
            onSelect();
        }
    };


    const cardStyle = {
        backgroundColor: active ? 'gray-200' : 'red',
    };
    return (
        <>
            <div className={categoryCardClassName} onClick={handleSelect} style={cardStyle}>
                {name}</div>
        </>
    )
}

export default CategoryCard;
