import React, { useState } from "react";

import { Link, useNavigate } from "react-router-dom";
import { PhoneVerification } from "../../interface/request/login/PhoneVerification";
import { CodeVerification } from "../../interface/request/login/CodeVerification";
import { RegistrationDtoRequest } from "../../interface/request/login/RegistrationDtoRequest";
import { api } from "../../utils/AxiosUtil";
import { LocalStorageUtil } from "../../utils/LocalStorageUtil";

export default function SignUp() {
  const navigate = useNavigate();
  const [verifyNumber, setVerifyNumber] = useState(true);
  const [error, setError] = useState(false);

  const [errorRegistration, setErrorRegistration] = useState(false);

  const [phoneCode, setPhoneCode] = useState("");

  const response = { code: "", token: "" };

  const [codeError, setCodeError] = useState(false);

  const [signUp, setSignUp] = useState({
    name: "",
    phone: "",
    password: "",
    confirmPassword: "",
  });

  function Verify() {
    setVerifyNumber(false);
  }

  const registration = async (response: any) => {
    const codeVerification = "/api/v1/otp/send";

    const verifyRequest: CodeVerification = {
      phone: signUp.phone,
      otp_type: 2,
    };

    const pathVerification = "/api/v1/otp/verify";

    await api
      .post(codeVerification, verifyRequest)
      .then((data) => {
        response.code = data.data.data.code;
        console.log(response);
        setVerifyNumber(false);
      })
      .catch(function (error) {
        console.log(error);
        setErrorRegistration(true);
      });

    const verificationRequest: PhoneVerification = {
      phone: signUp.phone,
      code: response.code,
      otp_type: 2,
    };
    console.log(verificationRequest);
    await api
      .post(pathVerification, verificationRequest)
      .then((data) => {
        LocalStorageUtil.setVerifyToken(data.data.data.token);
        console.log(LocalStorageUtil.getVerifyToken());
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const finalRegistration = async (response: any) => {
    console.log(2223);
    const path = "/api/v1/register";

    let token = LocalStorageUtil.getVerifyToken();

    if (token !== null) {
      const request: RegistrationDtoRequest = {
        token: token,
        phone: signUp.phone,
        password: signUp.password,
        confirm_password: signUp.confirmPassword,
      };

      console.log(request);

      await api
        .post(path, request)
        .then((data) => {
          console.log(data);

          if (data.status == 200) {
            navigate("/home");
          } else {
            setCodeError(true);
          }
        })
        .catch(function (error) {
          console.log(error);
        });
    }
  };

  return (
    <div className="h-screen  flex items-center justify-center flex-col  bg-gradient-to-r from-pink-100 via-purple-100 to-gray-100 w-full h-screen font-abel">
      {verifyNumber && (
        <>
          <div className="relative pb-10">
            <div className=" font-[1000]  text-center flex items-center">
              <div className="text-8xl text-text-col"> NU</div>
              <svg
                className="w-20 h-20"
                viewBox="0 0 56 59"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M52.4647 34.9356L52.5796 34.9931C55.2515 36.4871 56.2858 39.8485 54.878 42.5491C49.9939 51.8863 40.1683 58.2644 28.8774 58.1782C13.1334 58.0633 0.233603 45.2497 0.00376275 29.5057C-0.254807 13.2445 12.8461 0 29.0785 0C38.2434 0 46.4314 4.25204 51.7464 10.8887L41.3462 16.5197C38.0135 13.2732 33.388 11.3196 28.3315 11.5207C19.0517 11.8942 11.5819 19.5651 11.467 28.8736C11.3521 38.6993 19.2816 46.6862 29.0498 46.6862C35.83 46.6862 41.7197 42.8651 44.6501 37.234C46.1441 34.4185 49.6779 33.3842 52.4647 34.9356Z"
                  fill="#123499"
                />
                <path
                  d="M54.9643 15.83C56.2571 18.3583 55.3378 21.4611 52.8383 22.8401L46.4602 26.3739L30.6587 35.1366C28.188 36.5156 25.0851 35.8261 23.4188 33.5277L23.3613 33.4415C21.379 30.7122 22.2408 26.8336 25.2288 25.2247L41.375 16.4908L51.7753 10.8597C52.9819 12.4112 54.0449 14.0775 54.9643 15.83Z"
                  fill="#152238"
                />
              </svg>
              <div className="text-text-vents text-7xl mt-auto">vents</div>
            </div>
          </div>

          <form className="w-1/3 h-3/6 text-center">
            <div className="text-center text-3xl mb-4">Sign Up</div>
            {errorRegistration && (
              <p style={{ color: "red" }}>Phone number already registered!</p>
            )}
            <div className="w-8/12 h-1/6 border-2 solid m-auto">
              <input
                type="text"
                placeholder="Name"
                className=" w-full h-full border-2 solid border-bor-in rounded-xl  pl-11 text-xl mb-3"
                value={signUp.name}
                onChange={(event) => {
                  setSignUp({ ...signUp, name: event.target.value });
                }}
              />
              <input
                type="text"
                placeholder="Phone number..."
                className=" w-full h-full border-2 solid border-bor-in rounded-xl  pl-11 text-xl mb-3"
                value={signUp.phone}
                onChange={(event) => {
                  setSignUp({ ...signUp, phone: event.target.value });
                }}
              />
              <input
                type="text"
                placeholder="Your password"
                className=" w-full h-full border-2 solid border-bor-in rounded-xl  pl-11 text-xl mb-3"
                value={signUp.password}
                onChange={(event) => {
                  setSignUp({ ...signUp, password: event.target.value });
                }}
              />
              <input
                type="text"
                placeholder="Confirm password"
                className=" w-full h-full border-2 solid border-bor-in rounded-xl  pl-11 text-xl mb-5"
                value={signUp.confirmPassword}
                onChange={(event) => {
                  setSignUp({ ...signUp, confirmPassword: event.target.value });
                }}
              />

              <div className=" w-3/4 h-12 m-auto mb-12">
                <button
                  type="button"
                  className=" w-full h-full bg-dark-blue text-center text-white rounded-xl flex justify-center items-center"
                  onClick={registration}
                >
                  SIGN UP
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke-width="1.5"
                    stroke="currentColor"
                    className="w-5 h-5 absolute ml-48"
                  >
                    <path
                      stroke-linecap="round"
                      stroke-linejoin="round"
                      d="M13.5 4.5 21 12m0 0-7.5 7.5M21 12H3"
                    />
                  </svg>
                </button>
              </div>
              <div>
                Already have an accound? <Link to="/sign-in">Sign In</Link>
              </div>
            </div>
          </form>
        </>
      )}
      {!verifyNumber && (
        <>
          <div className="text-center w-1/2  h-2/5">
            <div className="text-5xl mb-4">Verify Your number</div>
            <div className="text-3xl">
              Enter the 4 digit code we sent to your phone.
            </div>
            {codeError && (
              <p style={{ color: "red" }}>Phone number already registered!</p>
            )}
            <div className="w-full h-1/2 flex items-center justify-center ">
              <input
                className="w-2/12 h-3/6 mx-5 text-4xl pl-6 rounded-xl"
                type="text"
                maxLength={4}
                value={phoneCode}
                onChange={(event) => {
                  setPhoneCode(event.target.value);
                }}
                /*{onKeyDown={(event) => {
                  if (!/[0-9]/.test(event.key)) {
                    event.preventDefault();
                  }
                }}}*/
              />
            </div>

            <div className=" w-4/12 h-12 m-auto mb-4">
              <button
                type="button"
                className=" w-full h-full bg-dark-blue text-center text-white rounded-xl flex justify-center items-center"
                onClick={
                  //navigate("/home");
                  finalRegistration
                }
              >
                VERIFY
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
}
