import React, { useState, useRef, ChangeEvent } from "react";

interface PhoneVerificationModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const Verify: React.FC<PhoneVerificationModalProps> = ({ isOpen, onClose }) => {
  const [verificationCode, setVerificationCode] = useState(["", "", "", ""]);
  const inputRefs = [
    useRef<HTMLInputElement>(null),
    useRef<HTMLInputElement>(null),
    useRef<HTMLInputElement>(null),
    useRef<HTMLInputElement>(null),
  ];

  const handleInputChange = (index: number, value: string) => {
    const newVerificationCode = [...verificationCode];
    newVerificationCode[index] = value;
    setVerificationCode(newVerificationCode);

    if (index < inputRefs.length - 1 && value !== "") {
      inputRefs[index + 1].current?.focus();
    }
  };

  const handleBackspace = (index: number) => {
    if (index > 0) {
      inputRefs[index - 1].current?.focus();
    }
  };

  return (
    <>
      {isOpen && (
        <div className="fixed inset-0 flex items-center justify-center">
          <div
            className="bg-gray-800 bg-opacity-50 fixed inset-0"
            onClick={onClose}
          ></div>
          <div className="bg-white p-6 rounded-md shadow-md">
            <h2 className="text-2xl font-bold mb-4">Phone Verification</h2>
            <div className="flex space-x-2">
              {verificationCode.map((digit, index) => (
                <input
                  key={index}
                  ref={inputRefs[index]}
                  type="text"
                  maxLength={1}
                  value={digit}
                  onChange={(e: ChangeEvent<HTMLInputElement>) =>
                    handleInputChange(index, e.target.value)
                  }
                  onKeyUp={(e) => {
                    if (e.key === "Backspace") {
                      handleBackspace(index);
                    }
                  }}
                  className="w-10 h-10 border border-gray-300 rounded-md text-center"
                />
              ))}
            </div>
            <button
              onClick={onClose}
              className="mt-4 bg-blue-500 text-white p-3 rounded-md hover:bg-blue-600 cursor-pointer"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default Verify;
