import React, { useState } from 'react';

export interface Category {
    id?: number;
    name: string;
    active?: string;
    status?: boolean;
    onSelect?: () => void;
}





const CategoryCard = ({ id, name, active, onSelect }: Category) => {
    const [sign, setSign] = useState('');
    console.log(active)
    const handleStatus = (active: string | undefined) => {
        if(active === 'plus'){
            setSign('+');
        }else if(active === 'minus'){
            setSign('-')
        }else if(active === undefined) {
            setSign('')
        }
    }

    return (
        <>
            <div className='bg-gray-200 rounded-[15px] min-w-[30px] max-w-[200px] p-3 h-[35px] flex items-center justify-center' onClick={onSelect}>
                {name} {sign}</div>

        </>
    )
}

export default CategoryCard;
