import React, { useState } from "react";
import { Link } from "react-router-dom";
import ReactInputMask from "react-input-mask";

export default function ForgotPassword() {
  return (
    <>
      <div className="h-screen flex items-center justify-center flex-col  bg-gradient-to-r from-pink-100 via-purple-100 to-gray-100 w-full font-abel">
        <div className="w-[500px] h-[500px] flex flex-col justify-center border-2 border-solid p-[15px]">
          <div className="text-3xl font-bold">Forgot your password?</div>
          <div className="text-2xl">
            Please enter your phone number to sign in NU event.
          </div>
          <InputMask
            mask="+7(999) 999-99-99"
            maskChar=""
            type="text"
            placeholder="+7(999)999 99 99"
            value={login.phone}
            onChange={(event) => {
              const numericValue = event.target.value.replace(/[\D\s]/g, "");
              setLogin({ ...login, phone: numericValue });
              if (error === true) {
                setError(false);
              }
            }}
            className={`w-full h-full border-2 solid ${
              error ? "border-red-500" : "border-bor-in"
            } rounded-xl pl-11 text-xl`}
            required
          />
          <button className="">Request password reset</button>
          <div>
            Already have an accound? <Link to="/sign-in">Sign In</Link>
          </div>
        </div>
      </div>
    </>
  );
}
