import React from 'react';

export interface Category {
    id?: number;
    name: string;
    active?: boolean;
    onSelect?: () => void;
}

const CategoryCard = ({ id, name, active, onSelect }: Category) => {
    return (
        <>
            <div className='bg-gray-200 rounded-[15px] min-w-[30px] max-w-[200px] p-3 h-[35px] flex items-center justify-center' onClick={onSelect}>
                {name} {active && active ? '+' : '-'}</div>
        </>
    )
}

export default CategoryCard;
