import React from "react";
import { create } from "zustand";
import { persist } from "zustand/middleware";

interface Token {
  jwt: string;
  setToken: (token: string) => void;
  refreshToken: () => void;
}

export const useTokenStore = create<Token>()(
  persist(
    (set, get) => ({
      jwt: " ",
      setToken: (token: string) => {
        set({ jwt: token });
      },
      refreshToken: () => {
        const timeout = 30 * 60 * 1000;
        setTimeout(() => {
          set({ jwt: " " });
        }, timeout);
      },
    }),
    { name: "jwt-storage" }
  )
);
