import React,{ useState, useEffect } from 'react';
import {CategoryService} from "../../../service/Categories/CategoryService";


const EventCategories = () => {
    const [categories, setCategories] = useState<Category[]>([]);

    useEffect(() => {
       CategoryService.getCategories()
           .then((data)=> setCategories(data.data.data.categories))

    }, []);

    const firstCategory = categories.slice(0,6);

    return (
        <>
            <div className='mx-auto w-[1000px] font-inter'>
                {firstCategory.map((category)=>
                    category.name
                )}
            </div>
        </>
    )
}

export default EventCategories;

