import React, { useState } from 'react';

export interface Category {
    id?: number;
    name: string;
    active?: boolean;
    onSelect?: () => void;
    categoryCardClassName?: string;
}

const CategoryCard = ({ name, active,  onSelect, categoryCardClassName }: Category) => {
    return (
        <>
            <div className={categoryCardClassName} onClick={onSelect} style={{ background: active ? 'white' : 'gray'}}>
                {name} + {active}</div>
        </>
    )
}

export default CategoryCard;
